mapper.select:
/* kr.kosmo.jobkorea.commonbiz.dao.MsgMgmtDao.selectToInfoWrqPscg */
SELECT	AB.EML AS TO_EML_ADRS
	,	CONCAT(AB.CPN_1, AB.CPN_2, AB.CPN_3) AS TO_PNN
FROM	tn_wrq_pscg_info AA
		INNER JOIN tn_usr_dtl_info AB ON AA.PSCG_SST_ID = AB.USR_SST_ID
WHERE	AA.WRQ_ID = #{wrq_id}

/* kr.kosmo.jobkorea.commonbiz.dao.MsgMgmtDao.selectToInfoOfc */ SELECT '' AS TO_EML_ADRS , CONCAT(AB.PNN_1, AB.PNN_2, AB.PNN_3) AS TO_PNN FROM tm_wrq_mst AA INNER JOIN tn_ofc_psnl AB ON AA.OFC_PSNL_ID = AB.OFC_PSNL_ID WHERE AA.WRQ_ID = #{wrq_id}
UNION ALL
SELECT AB.EML AS TO_EML_ADRS , CONCAT(AB.PNN_1, AB.PNN_2, AB.PNN_3) AS TO_PNN FROM tm_wrq_mst AA INNER JOIN tn_ofc_mgr AB ON AA.OFC_ID = AB.OFC_ID WHERE AA.WRQ_ID = #{wrq_id}

/* kr.kosmo.jobkorea.commonbiz.dao.MsgMgmtDao.selectToInfoOfcC_ */
SELECT	AB.EML AS TO_EML_ADRS
	,	CONCAT(AB.PNN_1, AB.PNN_2, AB.PNN_3) AS TO_PNN
FROM	tm_odr_mst AA
		INNER JOIN tn_ofc_mgr AB ON AA.OFC_ID = AB.OFC_ID
WHERE	AA.ODR_ID = #{odr_id}

/* kr.kosmo.jobkorea.commonbiz.dao.MsgMgmtDao.selectToInfoOfcM_ */ SELECT BD.EML AS TO_EML_ADRS , CONCAT(BD.CPN_1, BD.CPN_2, BD.CPN_3) AS TO_PNN FROM tn_mak_cmt BA INNER JOIN tm_wrq_mst BB ON BA.PTET_ID = BB.PTET_ID INNER JOIN tn_wrq_pscg_info BC ON BB.WRQ_ID = BC.WRQ_ID INNER JOIN tn_usr_dtl_info BD ON BC.PSCG_SST_ID = BD.USR_SST_ID INNER JOIN tm_usr_mst BE ON BD.USR_SST_ID = BE.USR_SST_ID WHERE BA.OFC_ID != BE.OFC_ID AND BA.CMT_ID = #{cmt_id}
UNION ALL
SELECT BC.EML AS TO_EML_ADRS , CONCAT(BC.PNN_1, BC.PNN_2, BC.PNN_3) AS TO_PNN FROM tn_mak_cmt BA INNER JOIN tm_ptet_mst BB ON BA.PTET_ID = BB.PTET_ID INNER JOIN tn_ofc_mgr BC ON BB.OFC_ID = BC.OFC_ID WHERE BA.OFC_ID != BC.OFC_ID AND BA.CMT_ID = #{cmt_id}
UNION ALL
SELECT '' AS TO_EML_ADRS , CONCAT(BC.PNN_1, BC.PNN_2, BC.PNN_3) AS TO_PNN FROM tn_mak_cmt BA INNER JOIN tm_ptet_mst BB ON BA.PTET_ID = BB.PTET_ID INNER JOIN tn_ofc_psnl BC ON BB.OFC_PSNL_ID = BC.OFC_PSNL_ID WHERE BA.OFC_ID != BC.OFC_ID AND BA.CMT_ID = #{cmt_id}
