mapper.update:
/*kr.kosmo.jobkorea.manageA.dao.LectureMgtDao.updateLectureMgt*/
UPDATE tb_tcourse  SET
		restyn = 'y'

WHERE	no = #{no}
and		loginID=#{loginID}

mapper.select:
/*kr.kosmo.jobkorea.manageA.dao.LectureMgtDao.listLectureMgtDtl*/ SELECT @rownum:=@rownum+1 as row_num , TC.no , TC.loginID , LC.title , LC.attend , LC.startdate , LC.enddate , TC.restyn FROM tb_tcourse TC,tb_lecture LC WHERE (@rownum:=0)=0
AND TC.no = #{no} AND TC.restyn = 'n' AND TC.no = LC.no
ORDER BY TC.no ASC
LIMIT #{pageIndex}, #{pageSize}

/*kr.kosmo.jobkorea.manageA.dao.LectureMgtDao.countListLectureMgt*/ SELECT COUNT(1) AS tot_cnt FROM ( SELECT @rownum:=@rownum+1 as row_num , TC.no , TC.loginID , LC.title , LC.attend , LC.startdate , LC.enddate , TC.restyn FROM tb_tcourse TC,tb_lecture LC WHERE (@rownum:=0)=0
AND TC.no = #{no} AND TC.restyn = 'n' AND TC.no = LC.no
ORDER BY TC.no ASC )FD
