mapper.select:
/*kr.kosmo.jobkorea.manageC.dao.lectureHDHDao.countListLectureGrp*/ SELECT count(*) FROM ( SELECT l.no FROM tb_process as p INNER JOIN tb_lecture as l ON p.no = l.no INNER JOIN tb_tcourse as t ON l.no = t.no INNER JOIN tb_userinfo as u ON t.loginID = u.loginID WHERE 1=1
AND l.title = #{keyword}
AND u.name = #{keyword}
AND l.plan = #{keyword}
AND l.startdate >= STR_TO_DATE(#{startDate}, '%Y-%m-%d') AND l.enddate <= STR_TO_DATE(#{endDate}, '%Y-%m-%d')
GROUP BY l.no ) AS dummy

mapper.update:
/*kr.kosmo.jobkorea.manageC.dao.lectureHDHDao.updateLectureGrp*/
UPDATE	tb_process as p, tb_lecture as l
SET		p.processrate = #{grp_pro_processRate}
	,	l.attend = #{grp_lec_attend}
	,	l.lateness = #{grp_lec_lateness}
	,	l.absent = #{grp_lec_absent}
WHERE	p.no = l.no 
	AND	p.no = #{grp_lec_no}
	AND	l.no = #{grp_lec_no}
