mapper.select:
/*kr.kosmo.jobkorea.supportA.dao.LectureMgtDao.listAwardMgt*/ SELECT @rownum:=@rownum+1 as row_num , no , title , startdate , enddate FROM tb_lecture WHERE (@rownum:=0)=0
AND title LIKE concat('%', #{title}, '%')
ORDER BY row_num DESC LIMIT #{pageIndex}, #{pageSize}

/*kr.kosmo.jobkorea.supportA.dao.AwardMgtDao.countListAwardMgt*/ SELECT COUNT(1) AS tot_cnt FROM (SELECT @rownum:=@rownum+1 as row_num , no , title , startdate , enddate FROM tb_lecture WHERE (@rownum:=0)=0
AND title LIKE concat('%', #{title}, '%')
ORDER BY row_num DESC)AA

mapper.update:
update tb_certinfo
	set no = #{no}, loginID = #{loginID},seq = #{seq},reg_date = #{reg_date} 
where no = #{no}
AND		loginID= #{loginID}
