mapper.select:
/*kr.kosmo.jobkorea.adm.dao.AdmProjectDao.selectAdmList*/

SELECT  pi.project_name
		       ,pi.area
		       ,pi.work_type
		       ,pi.industry_type
		       ,pi.reg_date
		       ,pi.receive_fr_date
		       ,pi.receive_to_date
		       ,pi.regId
          from tb_project_list pi
            WHERE	pi.area = #{area}
			   AND  pi.work_type = #{work_type}
			   AND  pi.industry_type = #{industry_type}
