mapper.select:
SELECT @num:=@num+1 as wno2 , wno , writer , title , content , read_cnt , reg_date FROM tb_freeboard WHERE (@num:=#{pageIndex})=#{pageIndex}
AND title LIKE concat('%', #{title1}, '%')
AND reg_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
AND reg_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
ORDER BY reg_date DESC LIMIT #{pageIndex}, #{pageSize}

mapper.update:
update tb_freeboard
	set title = #{title}, content = #{content} , reg_date = now()
where wno = #{wno}
