mapper.select:
/*kr.kosmo.jobkorea.pc.dao.ProductDao.listProduct*/ SELECT @rownum:=@rownum+1 as row_num , food_id , food_type , food_type2 , food_name , sale_price , stock_count
FROM tb_food
WHERE (@rownum:=0)=0
AND food_type2 = #{type}
AND food_name like CONCAT('%',#{keyword},'%')
ORDER BY CAST(food_id AS signed integer) DESC LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
/*kr.kosmo.jobkorea.pc.dao.ProductDao.insertProduct*/
INSERT INTO tb_food
(
		food_id, food_type, food_type2, food_name, sale_price, stock_count
)
VALUES
( 
		(SELECT Max( CAST(food_id AS signed integer))+1 FROM tb_food a) 			
	,	#{food_type}
	,	#{food_type2}
	,	#{food_name}
	,	#{sale_price}
	,	#{stock_count}		
)

mapper.update:
/*kr.kosmo.jobkorea.pc.dao.ProductDao.updateProduct*/
UPDATE tb_food SET
		food_type2 = #{food_type2}				
	,	food_name = #{food_name}
	,	sale_price = #{sale_price}			
	,	stock_count = #{stock_count}
WHERE	food_id = #{food_id}
