mapper.select:
/*kr.kosmo.jobkorea.supportA.dao.AdviceMgtDao.listAdviceMgt*/ SELECT @rownum:=@rownum+1 as row_num , no , title , startdate , enddate FROM tb_lecture WHERE (@rownum:=0)=0
AND title LIKE concat('%', #{title}, '%')
ORDER BY row_num DESC LIMIT #{pageIndex}, #{pageSize}

/*kr.kosmo.jobkorea.supportA.dao.AdviceMgtDao.listAdviceMgtDtl*/
SELECT	@rownum:=@rownum+1 as row_num	
	,	no
	,	loginID
	,	score
FROM 	tb_testanswer		
WHERE	(@rownum:=0)=0
	AND no = #{no}
ORDER BY no ASC

LIMIT #{pageIndex}, #{pageSize}

mapper.update:
update tb_counsel
	set counsel = #{counsel} 
where loginID = #{loginID}
