mapper.select:
select count(*) as total_cnt from (SELECT @num:=@num+1 as nt_no2 , nt_no , nt_title , nt_note , write_date , loginID FROM tb_notice2 WHERE (@num:=#{pageIndex})=#{pageIndex}
AND nt_title LIKE concat('%', #{title}, '%')
AND write_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
AND write_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
)a

SELECT @num:=@num+1 as nt_no2 , nt_no , nt_title , nt_note , write_date , loginID FROM tb_notice2 WHERE (@num:=#{pageIndex})=#{pageIndex}
AND nt_title LIKE concat('%', #{title}, '%')
AND write_date >= STR_TO_DATE(#{from_date}, '%Y-%m-%d')
AND write_date <= STR_TO_DATE(#{to_date}, '%Y-%m-%d')
ORDER BY write_date DESC LIMIT #{pageIndex}, #{pageSize}
